package com.human_developing_soft.unitconverter.domainimport androidx.lifecycle.ViewModelimport com.human_developing_soft.unitconverter.ui.converting.view_wrappers.BMEventListenerimport com.human_developing_soft.unitconverter.ui.converting.view_wrappers.BundleManagerResponseclass UnitConvertingVM(    private val mObserver: StateEventListener,    private val mUnitsList: UnitList,) : ViewModel(), BMEventListener {    fun unitNames(): Array<String> {        return mUnitsList.unitsName()    }    override fun onUiUpdate(content: BundleManagerResponse) {        val convertingObject = mUnitsList.convertingObjectForResponse(                content            )        val valuesList = convertingObject.convertAll()        val indexList = mutableListOf<Int>()        for (i in valuesList.indices) {            indexList.add(                content                    .content(i)                    .selectorPosition()            )        }        mObserver.updateState(            StateUpdatedContent.Base(                valuesList, indexList            )        )    }}