package com.human_developing_soft.unitconverter.domainimport androidx.lifecycle.ViewModelimport com.human_developing_soft.unitconverter.ui.converting.view_wrappers.UiContentHolderclass UnitConvertingVM(    private val mUnits: UnitList) : ViewModel(), ConvertingViewModel {    private val mHandler = ConvertingHandler.Base()    override fun addObserver(observer: ConvertingEventListener) {        mHandler.addObserver(observer)    }    override fun removeObserver(observer: ConvertingEventListener) {        mHandler.removeObserver(observer)    }    override fun unitNames() = mUnits.unitsName()    override fun onChanged(uiState: UiContentHolder) {        mHandler.implementCalculations(            PreparedContent.Base(                mUnits,                uiState            )        )    }}