package com.human_developing_soft.unitconverter.domainimport androidx.lifecycle.ViewModelimport com.human_developing_soft.unitconverter.ui.converting.view_wrappers.UiContentHolderclass UnitConvertingVM(    private val mUnits: UnitList) : ViewModel(), ConvertingViewModel, UiEventListener {    private val mHandler = ConvertingHandler.Base(mUnits)    override fun onUiUpdated(updatedList: UiContentHolder) {        mHandler.implementCalculations(            PreparedContent.Base(                mUnits,                updatedList            )        )    }    override fun addObserver(observer: ConvertingEventListener) {        mHandler.addObserver(observer)    }    override fun removeObserver(observer: ConvertingEventListener) {        mHandler.removeObserver(observer)    }}