package com.human_developing_soft.unitconverter.domainimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.human_developing_soft.unitconverter.units_storage.data_loading.UnitDataJSONimport com.human_developing_soft.unitconverter.units_storage.data_loading.UnitDataReaderimport com.human_developing_soft.unitconverter.units_storage.data_loading.UnitsDataLoaderclass UnitConvertingVMFactory(    private val mObserver: StateEventListener,    private val mJsonFile: UnitDataJSON,) : ViewModelProvider.Factory {    @Suppress("UNCHECKED_CAST")    override fun <T : ViewModel?> create(modelClass: Class<T>): T {        val unitData = UnitsDataLoader.Base(            UnitDataReader.Base(                mJsonFile            )        ).unitsData()        return UnitConvertingVM(            mObserver,            UnitList.Base(                unitData            )        ) as T    }}