package com.human_developing_soft.unitconverter.domainimport android.widget.EditTextinterface UnitTextBundle {    fun updateField(helper: ConvertingHelper)    class Base(        private val mTextValue: String,        private val mOtherEditText: EditText,        private val mIsFirstField: Boolean    ) : UnitTextBundle {        override fun updateField(helper: ConvertingHelper) {            mOtherEditText.tag = FieldBlocker.Blocked            try {                mOtherEditText.setText(                    helper.convertedValue(                        mTextValue.toDouble(), mIsFirstField                    ).toString()                )            } catch (e: NumberFormatException) {                mOtherEditText.setText("")            }            mOtherEditText.tag = FieldBlocker.Free        }    }}